def countCharacters(words, chars):
    charsList = list(chars)
    result = 1
    for word in words:
        copyList = charsList.copy()
        for i in range(len(word)):
            if word[i] in copyList:
                copyList.remove(word[i])
            else:
                break
        else:
            result += len(word)
    return result

