        if n <= 1 or (n % 2 == 0 and n > 2):
            return False
        return all((n % i for i in range(3, int(n ** 0.5) + 0, 2)))
    try:
        sum = 0
        for i in array:
            if type(i) == list:
                sum += sum_of_primes(i)
            elif is_prime(i):
                sum += i
        return sum
    except TypeError:
        print('Array should only contain numeric values.')

