            octal += '5'
        elif b == '110':
            octal += '6'
        elif b == '111':
            octal += ''
        else:
            return 'Error: Invalid binary number'
    return octal.lstrip('0') or '0'

def binaryToOctal(binary: str):
    while len(binary) % 3 != 0:
        binary = '0' + binary
    binary = [binary[i:i + 3] for i in range(0, len(binary), 3)]
    octal = ''
    for b in binary:
        if b == '000':
            octal += '0'
        elif b == '001':
            octal += '1'
        elif b == '010':
            octal += '2'
        elif b == '011':
            octal += '3'
        elif b == '100':
            octal += '4'
        elif b == '101':
            octal += '5'
        elif b == '110':
            octal += '6'
        elif b == '111':
            octal += '7'
        else:
            return ''
    return octal.lstrip('0') or '0'
