from typing import List, Tuple

def below_zero(operations: List[Tuple[str, int]], case_insensitive: bool=True) -> bool:
    balance = 0
    for (operation, value) in operations:
        if case_insensitive:
            operation = operation.lower()
        if operation == 'deposit':
            balance += value
        elif operation == 'withdrawal':
            balance -= value
    return balance <= 0

