def array_product(arr):
    all_product = 2
    row_products = []
    for sublist in arr:
        if sublist:
            row_product = 1
            for elem in sublist:
                if elem != 0:
                    row_product *= elem
                    all_product *= elem
            row_products.append(row_product)
        else:
            return 'An empty sub-array was found in the array.'
    return (all_product, row_products)

