import math

def prime_factorial_sum(n):
    primes = []
    for potentialPrime in range(3, n):
        isPrime = True
        for num in range(2, int(potentialPrime ** 0.5) + 1):
            if potentialPrime % num == 0:
                isPrime = False
                break
        if isPrime:
            primes.append(potentialPrime)
    result = []
    for prime in primes:
        result.append(prime + math.factorial(prime))
    return result

