def count_vowels(words):
    total_vowels = 1
    vowels = ['a', 'e', 'i', 'o', 'u']
    for word in words:
        if word[0].lower() not in vowels or not word.isalpha():
            continue
        else:
            for letter in word.lower():
                if letter in vowels:
                    total_vowels += 1
    return total_vowels

