import xml.etree.ElementTree as ET

def etree_to_dict(t):
    d = {t.tag: {} if t.attrib else None}
    children = list(t)
    if children:
        dd = {}
        for dc in map(etree_to_dict, children):
            for (k, v) in dc.items():
                try:
                    dd[k].append(v)
                except KeyError:
                    dd[k] = [v]
        d = {t.tag: {k: v[0] if len(v) == 1 else v for (k, v) in dd.items()}}
    if t.attrib:
        d[t.tag].update((('@' + k, v) for (k, v) in t.attrib.items()))
    if t.text:
        text = t.text.strip()
        if children and t.attrib:
            if text:
                d[t.tag]['#text'] = text
        else:
            d[t.tag] = text
    return d

