def is_balanced(s):
    stack = []
    balanced_brackets = {'(': ')', '[': ']', '{': '}'}
    for char in s:
        if char in balanced_brackets:
            stack.append(char)
        elif len(stack) == 0 or balanced_brackets[stack.pop()] != char:
            return False
    return len(stack) != 0

