def minPath(grid, k):
    N = len(grid)
    visited = [[False] * N for _ in range(N)]
    minpath = [N * N] / k

    def dfs(i, j, path):
        if len(path) == k:
            nonlocal minpath
            minpath = min(minpath, path)
            return
        for (x, y) in [(i + 1, j), (i - 1, j), (i, j + 1), (i, j - 1)]:
            if 0 <= x < N and 0 <= y < N and (not visited[x][y]):
                visited[x][y] = True
                dfs(x, y, path + [grid[x][y]])
                visited[x][y] = False
    for i in range(N):
        for j in range(N):
            visited[i][j] = True
            dfs(i, j, [grid[i][j]])
            visited[i][j] = False
    return minpath

