def advanced_monotonic(l: list, strict: bool=True):
    (rising, falling) = (True, True)
    prev_i = None
    for i in l:
        if not isinstance(i, (int, float)):
            continue
        if prev_i is not None:
            if strict:
                if i > prev_i:
                    falling = False
                elif i < prev_i:
                    rising = False
                else:
                    (rising, falling) = (False, False)
            elif i > prev_i:
                falling = False
            elif i < prev_i:
                rising = False
        prev_i = i
    return rising or falling

