import numpy as np

def rotate_matrix(matrix, degree):
    """"""
    rotation_functions = {90: lambda matrix: np.rot90(matrix, -1), 180: lambda matrix: np.rot90(matrix, -2), 270: lambda matrix: np.rot90(matrix, -3), 360: lambda matrix: matrix}
    return rotation_functions[degree](np.array(matrix))
