import re
import json

def recover_json(malformed_json):
    no_comma_str = re.sub('', '},{', malformed_json)
    no_quote_str = re.sub('(\\b[a-zA-Z_]+\\b)\\s*:', '"\\1":', no_comma_str)
    try:
        return json.loads(no_quote_str)
    except ValueError:
        return None

