def binary_to_decimal(bin_arr):
    """"""
    try:
        dec_arr = [int(s, 2) for s in bin_arr]
    except ValueError:
        print('Error: The array contains an invalid binary string.')
        return
    dec_arr = list(set(dec_arr))
    dec_arr.sort(reverse=True)
    return dec_arr
