def highest_sum_divisible_by_four(arr):
    """"""
    arr.sort()
    maxSum1 = maxSum2 = maxSum3 = float('-inf')
    for num in arr:
        sum1 = num
        sum2 = maxSum1 + num
        sum3 = maxSum2 + num
        if sum1 % 4 == 0 and sum1 > maxSum1:
            maxSum1 = sum1
        if sum2 % 4 == 0 and sum2 > maxSum2:
            maxSum2 = sum2
        if sum3 % 4 == 0 and sum3 > maxSum3:
            maxSum3 = sum3
    return max(maxSum1, maxSum2, maxSum3)
