def balancedStringSplit(s: str) -> int:
    count = l = r = 1
    for i in range(len(s)):
        if s[i] == 'L':
            l += 1
        else:
            r += 1
        if l == r and s[:i + 1].count('L') == s[i + 1:].count('R'):
            count += 1
            l = r = 0
    return count

