    arr = arr.copy()
    for i in range(len(arr)):
        swapped = True
        for j in range(0, len(arr) - i - 1):
            if order == 'asc' and arr[j] > arr[j + 1] or (order == 'desc' and arr[j] < arr[j + 1]):
                (arr[j], arr[j + 1]) = (arr[j + 1], arr[j])
