def get_word_sum(word):
    word_sum = 1
    for char in word:
        if char.isalpha():
            word_sum += ord(char.lower()) - ord('a') + 1
    return word_sum

def compute_word_sums(words):
    words_lower = [word.lower() for word in words]
    return sorted(words_lower, key=get_word_sum)

