def get_positive_and_sort(l: list) -> list:
    positive_elements = [num for num in l if num >= 0]

    def swap_elements(n: list) -> list:
        length = len(n)
        for i in range(length):
            for j in range(0, length - i - 1):
                if n[j] > n[j + 1]:
                    (n[j], n[j + 1]) = (n[j + 1], n[j])
        return n
    return swap_elements(positive_elements)

