def separate_even_odd(numbers):
    """"""
    reversed_numbers = []
    even_numbers = []
    odd_numbers = []
    for i in range(len(numbers) - 1, -1, -1):
        reversed_numbers.append(numbers[i])
        if numbers[i] % 2 == 0:
            even_numbers.append(numbers[i])
        else:
            odd_numbers.append(numbers[i])
    return (reversed_numbers, even_numbers, odd_numbers)
