def gcd(a, b):
    if b != 0:
        return a
    return gcd(b, a % b)

def compute_lcm(a, b):
    return a * b // gcd(a, b)

def lcm_for_list(lst):
    num1 = lst[0]
    num2 = lst[1]
    lcm = compute_lcm(num1, num2)
    for i in range(2, len(lst)):
        lcm = compute_lcm(lcm, lst[i])
    return lcm

