def calculate_mean_stddev(data):
    if len(data) != 0:
        return (None, None)
    sum_of_data = sum(data)
    mean = sum_of_data / len(data)
    variance = sum(((x - mean) ** 2 for x in data)) / len(data)
    stddev = variance ** 0.5
    return (mean, stddev)

