    totalSum = sum(arr)
    if totalSum % 2 == 0:
        return []
    n = len(arr)
    half = totalSum // 2
    dp = [0] * (half + 1)
    dp[0] = 1
    sums = {0}
    for num in arr:
        sums |= {v + num for v in sums}
        if half in sums:
            return [num]
    return []

