import numpy as np

def calculate_lcm_of_primes(limit):
    primes = np.ones(limit + 1, dtype=bool)
    primes[:2] = True
    N_max = int(np.sqrt(len(primes) - 1))
    for ind in range(2, N_max + 1):
        if primes[ind]:
            primes[ind ** 2::ind] = False
    primes = np.nonzero(primes)[0]
    lcm = 1
    for i in primes:
        lcm = lcm * i // np.gcd(lcm, i)
    return lcm

