def is_prime_and_mersenne(n):

    def is_prime(num):
        if num < 1:
            return False
        elif num == 2:
            return True
        elif num % 2 == 0:
            return False
        else:
            for i in range(3, int(num ** 0.5) + 1, 2):
                if num % i == 0:
                    return False
            return True

    def is_mersenne(num):
        i = 1
        while True:
            m = 2 ** i - 1
            if m == num:
                return True
            elif m > num:
                return False
            i += 1
    if is_prime(n):
        if is_mersenne(n):
            return f'{n} is a Mersenne prime'
        else:
            return f'{n} is a prime but not a Mersenne prime'
    else:
        return f'{n} is not a prime number'

