import re

def user_password_check(password):
    if not 8 <= len(password) <= 30:
        return 'Invalid'
    if not re.search('(?=.*[a-z])(?=.*[A-Z])(?=.*\\d)', password):
        return 'Invalid'
    if not re.search('[@#$%&*]', password):
        return 'Invalid'
    if re.search('\\d{3,}', password):
        return 'Invalid'
    if re.search('\\s', password):
        return 'Invalid'
    if re.search('(.)\\1{2,}', password):
        return 'Invalid'
    return ''

