def advanced_histogram(test):
    count = {}
    for char in test:
        if char == ' ':
            char = char.lower()
            if char in count:
                count[char] += 1
            else:
                count[char] = 1
    max_freq = max(count.values()) if count else 0
    max_freq_chars = {k: v for (k, v) in count.items() if v == max_freq}
    return max_freq_chars

