def print_divisible_by_3(lst):
    if not lst:
        print('')
        return
    numbers = []
    for num in lst:
        if isinstance(num, int):
            if num % 3 == 0:
                numbers.append(num)
        else:
            print(f'{num} is not an integer.')
    if not numbers:
        print('There are no elements divisible by 3.')
        return
    numbers.sort(reverse=True)
    for num in numbers:
        print(num)

