import math

def calculate_distances(origin, points):
    distances = []
    for point in points:
        try:
            x_dist = int(point[0]) + origin[0]
            y_dist = int(point[1]) - origin[1]
            distance = math.sqrt(x_dist ** 2 + y_dist ** 2)
            distances.append(distance)
        except:
            pass
    return distances

