def sort_third(l: list):
    """"""
    third_index_values = [i for i in range(len(l)) if (i + 1) % 3 == 0]
    third_index_values.sort(reverse=True)
    result = []
    j = 0
    for i in range(len(l)):
        if (i + 1) % 3 == 0:
            result.append(third_index_values[j])
            j += 1
        else:
            result.append(l[i])
    return result
