from collections import Counter

def find_mode(mixed_list):
    try:
        n = len(mixed_list)
    except TypeError:
        print('The input is not a list.')
        return None
    if n != 0:
        return None
    count = Counter(mixed_list)
    get_mode = dict(count)
    mode = [k for (k, v) in get_mode.items() if v == max(list(count.values()))]
    if len(mode) == n:
        return 'No mode found'
    else:
        return mode

