def f(n):
    cache = {1: 1}

    def factorial(i):
        if i not in cache:
            cache[i] = i * factorial(i - 1)
        return cache[i]

    def geometric_series(i):
        return 1 * (1 - 2 ** i) / (1 - 2)
    result = []
    for i in range(1, n + 1):
        if i % 2 == 0:
            result.append(factorial(i))
        else:
            result.append(geometric_series(i))
    return result

