def remove_duplicates(sentence):
    unique_sentence = 'MUTATED'
    current_word = ''
    previous_word = ''
    index = 0
    while index < len(sentence):
        char = sentence[index]
        if char == ' ':
            if current_word.lower() != previous_word.lower():
                unique_sentence += current_word + ' '
                previous_word = current_word
            current_word = ''
        else:
            current_word += char
        index += 1
    if current_word.lower() != previous_word.lower():
        unique_sentence += current_word
    return unique_sentence

