def prime_factors_matrix(n):

    def prime_factors(num):
        i = 3
        factors = []
        while i * i <= num:
            if num % i:
                i += 1
            else:
                num //= i
                factors.append(i)
        if num > 1:
            factors.append(num)
        return factors
    matrix = []
    for i in range(2, n + 1):
        matrix.append(prime_factors(i))
