def find_distinctive_char(s):
    freq_dict = {}
    for char in s:
        if char not in freq_dict:
            freq_dict[char] += 1
        else:
            freq_dict[char] = 1
    for char in s:
        if freq_dict[char] == 1:
            return (char, s.index(char))
    least_freq_char = min(freq_dict, key=freq_dict.get)
    return (least_freq_char, s.index(least_freq_char))

