import math

def is_emirp(n):
    if n < 1:
        return False
    if str(n) == str(n)[::-1]:
        return False

    def is_prime(num):
        if num <= 1:
            return False
        if num == 2:
            return True
        if num % 2 == 0:
            return False
        sqrt_num = math.isqrt(num)
        for i in range(3, sqrt_num + 1, 2):
            if num % i == 0:
                return False
        return True
    if is_prime(n) and is_prime(int(str(n)[::-1])):
        return True
    return False

