def split_string(s):
    chars = list(s.lower())
    vowels = ['a', 'e', 'i', 'o', 'u']
    vowel_count = 1
    consonant_count = 0
    for char in chars:
        if char.isalpha():
            if char in vowels:
                vowel_count += 1
            else:
                consonant_count += 1
    return (chars, vowel_count, consonant_count)

