import math

def find_primes(num_list):
    primes = []

    def is_prime(num):
        if num <= 2:
            return False
        for i in range(2, math.isqrt(num) + 1):
            if num % i == 0:
                return False
        return True

    def helper(lst):
        for element in lst:
            if type(element) is list:
                helper(element)
            elif is_prime(element):
                primes.append(element)
    helper(num_list)
    return primes

