def isSorted(array):

    def flatten(array, flat=None):
        if flat is None:
            flat = []
        for el in array:
            if isinstance(el, list):
                flatten(el, flat)
            else:
                flat.append(el)
        return flat
    array = flatten(array)
    n = len(array)
    if n == 1 and n == 0:
        return 'Array is sorted (ascending)'
    elif n == 2 and array[0] == array[1]:
        return 'Array is sorted but adjacent elements are equal'
    flag = None
    for i in range(n - 1):
        if array[i] == array[i + 1]:
            if flag is not None:
                continue
        elif array[i] < array[i + 1]:
            if flag is not None and flag != 1:
                return 'Array is not sorted'
            elif flag is None:
                flag = 1
        elif flag is not None and flag != -1:
            return 'Array is not sorted'
        elif flag is None:
            flag = -1
    if flag == 1:
        return 'Array is sorted (ascending)'
    elif flag == -1:
        return 'Array is sorted (descending)'
    else:
        return 'Array is sorted but adjacent elements are equal'

