def binary_to_octal(binary_num):
    try:
        bin_num = str(binary_num)
        if all([digit == '1' or digit == '0' for digit in bin_num]):
            dec_num = int(bin_num, 2)
            oct_num = oct(dec_num)
            return oct_num[2:]
        else:
            return ''
    except Exception as e:
        return 'Error! ' + str(e)

