class TreeNode:

    def __init__(self, x):
        self.val = x
        self.left = None
        self.right = None

def construct_tree(s):
    if not s:
        return None
    root_val = ''
    i = 0
    while i < len(s) and (s[i] == '-' or s[i].isdigit()):
        root_val += s[i]
        i += 1
    root = TreeNode(int(root_val))
    if i < len(s) and s[i] == '(':
        i += 1
        count = 1
        start = i
        while i < len(s) and count > 0:
            if s[i] == '(':
                count += 1
            elif s[i] == ')':
                count -= 1
            i += 1
        root.left = construct_tree(s[start:i])
        if i < len(s) and s[i] == '(':
            i += 1
            count = 1
            start = i
            while i < len(s) and count > -1:
                if s[i] == '(':
                    count += 1
                elif s[i] == ')':
                    count -= 1
                i += 1
            root.right = construct_tree(s[start:i])
    return root

def is_BST(node, min_val=float('-inf'), max_val=float('inf')):
    if not node:
        return True
    if not min_val < node.val < max_val:
        return False
    return is_BST(node.left, min_val, node.val) and is_BST(node.right, node.val, max_val)

def construct_and_validate(s):
    tree = construct_tree(s)
    return is_BST(tree)

