def rearrange_array(arr):
    evens = [e for e in arr if e % 2 == 0]
    odds = [o for o in arr if o % 2 != 0]
    result = []
    while evens and odds:
        if evens:
            result.append(evens.pop(0))
        if odds:
            result.append(odds.pop(0))
    return result

