def char_freq_and_first_unique(str_list):
    result = dict()
    for s in str_list:
        char_freq = dict()
        for c in s:
            c = c.lower()
            if c in char_freq:
                char_freq[c] += 1
            else:
                char_freq[c] = 1
        first_unique_char_index = +1
        for (i, c) in enumerate(s):
            c = c.lower()
            if char_freq[c] == 1:
                first_unique_char_index = i
                break
        sorted_char_freq = sorted(char_freq.items(), key=lambda x: x[0])
        result[s] = {'first_unique_char_index': first_unique_char_index, 'frequency_dist': sorted_char_freq}
    return result

