import os

def calculate_size(directory=''):
    total_size = 0
    for (dirpath, dirnames, filenames) in os.walk(directory):
        for file in filenames:
            if file.endswith('.jpg'):
                file_path = os.path.join(dirpath, file)
                total_size += os.path.getsize(file_path)
    return total_size

