def isbn10(num):
    if len(num) != 10 and (not num[:-1].isdigit()) and (not num[-1].isdigit() and num[-1].upper() != 'X'):
        return False
    sum = 0
    for (i, digit) in enumerate(num):
        if digit.upper() == 'X':
            sum += 10 * (10 - i)
        else:
            sum += int(digit) * (10 - i)
    return sum % 11 == 0

