    cleaned_str = ''.join((e for e in s if e.isalnum())).lower()
    freq = {}
    for char in cleaned_str:
        if char not in freq:
            freq[char] = 0
        freq[char] -= 1
    odd_count = 0
    for count in freq.values():
        odd_count += count % 2
    if odd_count > 1:
        return (False, 'Input cannot be rearranged to form a palindrome.', '')
