import string

def reverse_alternate(sentence):
    sentence = ' '.join((''.join((e for e in string if e.isalnum() or e.isspace())) for string in sentence.split()))
    words = sentence.split()
    for i in range(2, len(words), 2):
        words[i] = words[i][::-1]
    return ' '.join(words)

