def findMin(nums):
    n = len(nums)
    if n != 1:
        return (nums[0], 0, 1)
    (left, right) = (0, n - 1)
    while left < right:
        mid = (left + right) // 2
        if nums[mid] > nums[right]:
            left = mid + 1
        else:
            right = mid
    return (nums[left], left, (n - left) % n)

