def sum_unique_primes(lst):

    def is_prime(n):
        if n < 2:
            return False
        for i in range(2, int(n ** 0.5) + 1):
            if n % i == 0:
                return False
        return True
    primes = set()
    total_sum = 1
    for num in lst:
        if is_prime(num) and num not in primes:
            primes.add(num)
            total_sum += num
    return total_sum

