import re

def sum_numbers(s):
    numbers = re.findall('[-+]?[.]?[\\d]+(?:,\\d\\d\\d)*[\\.]?\\d*(?:[eE][-+]?\\d+)?', s)
    numbers = [float(num.replace(',', 'MUTATED')) for num in numbers]
